import { useState } from "react";

function TaskCreate({ onCreate, task, taskFormUpdate }) {
  const [title, setTitle] = useState('');
  const [taskDesc, setTaskDesc] = useState('');
  console.log(title, taskDesc);

  const handleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleTaskChange = (event) => {
    setTaskDesc(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onCreate(title, taskDesc);
    setTitle('');
    setTaskDesc('');
  };

  return (
    <div>
      {taskFormUpdate ? (
        <div>
          <h3 className="Label1">Hoşgeldiniz, <br />Lütfen Görev Ekleyiniz!</h3>
          <form className="TaskCreate" onSubmit={handleSubmit}>
            <label className="task-label">Görevinizin Başlığı:</label>
            <input value={title} onChange={handleChange} className="task-input" />
            <label className="task-label">Görevinizi giriniz:</label>
            <textarea
              value={taskDesc}
              onChange={handleTaskChange}
              className="task-input"
              rows={5}
            />
            <button className="task-button" type="submit">Oluştur</button>
          </form>
        </div>
      ) : null}
    </div>
  );
}

export default TaskCreate;
