import { useEffect, useState } from 'react';
import { useTranslation } from 'react-i18next';
import i18n from 'i18next';

export default function ChangeLang() {
  const { t } = useTranslation();
  const [lang, setLang] = useState('en');

  useEffect(() => {
    // Initialize i18n and set the default language
    i18n.init({
      resources: {
        en: {
          translation: {
            // your translations for english
          }
        },
        sp: {
          translation: {
            // your translations for turkish
          }
        }
      },
      lng: 'en', // default language
      fallbackLng: 'en', // fallback language
      interpolation: {
        escapeValue: false // react already safes from xss
      }
    });
    setLang(i18n.language);
  }, []);

  const langChange = (e) => {
    setLang(e.target.value);
    i18n.changeLanguage(e.target.value);
  };

  return (
    <div>
      <select
        className="selectBox"
        onChange={langChange}
        name="lang"
        value={lang}
      >
        <option className="optionsMenu" value="en">
          English
        </option>
        <option className="optionsMenu" value="sp">
          Turkish
        </option>
      </select>
      <p className="textToChange">{t('translation_key_1')}</p>
      <p className="textToChange">{t('translation_key_2')}</p>
    </div>
  );
}
