import React, { useState, useEffect } from 'react';

const TaskCreate = ({ onCreate, onUpdate, taskToEdit }) => {
  const [title, setTitle] = useState('');
  const [taskDesc, setTaskDesc] = useState('');

  useEffect(() => {
    if (taskToEdit) {
      setTitle(taskToEdit.title);
      setTaskDesc(taskToEdit.taskDesc);
    }
  }, [taskToEdit]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (taskToEdit) {
      onUpdate(taskToEdit.id, title, taskDesc);
    } else {
      onCreate(title, taskDesc);
    }
    setTitle('');
    setTaskDesc('');
  };

  return (
    <div className="task-create">
      <h3>{taskToEdit ? 'Görevi Güncelle' : 'Yeni Görev Oluştur'}</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Başlık:
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </label>
        <label>
          Açıklama:
          <textarea
            value={taskDesc}
            onChange={(e) => setTaskDesc(e.target.value)}
          ></textarea>
        </label>
        <button type="submit">{taskToEdit ? 'Güncelle' : 'Oluştur'}</button>
      </form>
    </div>
  );
};

export default TaskCreate;
